isblank(AnnualRevenue),null,
if(
AnnualRevenue < 1000000,"$0-$1M",
if(
AnnualRevenue < 10000000,"$1M-$10M",
if(
AnnualRevenue < 50000000,"$10M-$50M",
if(
AnnualRevenue < 100000000,"$50M-$100M",
if(
AnnualRevenue < 250000000,"$100M-$250M",
if(
AnnualRevenue < 500000000,"$250M-$500M",
if(
AnnualRevenue < 1000000000,"$500M-$1B",
if(
AnnualRevenue < 10000000000,"$1B-$10B",
if(
AnnualRevenue >= 10000000000,"$10B+",
"unknown"
))))))))))
