CustomField.formula:
IF( OR(ISPICKVAL(Stage__c, 'Completed'), ISPICKVAL(Stage__c, 'Vendor Completed')), 'Fully Shipped',
IF( DueDate__c < TODAY(), TEXT(Job_Overdue_Flag__c) & ' ' & Days_Syntax__c & ' Late',
IF( DueDate__c = TODAY(), 'Due Today', 
'Due in ' & TEXT(ABS(Job_Overdue_Flag__c)) & ' ' & Days_Syntax__c)
) 
)
