CustomField.formula:
if (
  (LastReadJobDate__c >= today() - 30 && LastJobAcceptedDate__c < today() - 30), 'Lurker',
    if (
      ((LastReadJobDate__c < today() - 30 || ISNULL(LastReadJobDate__c)) && LastJobAcceptedDate__c < today() - 30), 'Dormant',
      if (
        (
          (ISPICKVAL(SupplierStatus__c, 'approved') || ISPICKVAL(SupplierStatus__c, 'pip')) && 
          (LastReadJobDate__c >= today() - 30 && LastJobAcceptedDate__c >= today() - 30)
        ), 'Active', null
      )
    )
)
